import React from 'react'
import './Education.css'
import { useState } from 'react'
export default function Education() {
    const EducationData=[
        {BTech:{
        college:"Gokaraju rangaraju Institue of Engineering & Technology",
        qualification:"BTech in Information Technology",
        details:{
        location:"Kukatpally, Hyderabad, Telangana",
        duration:"07-2021 - 07-2024",
        grade:"CGPA- 9.0"
        }
    }
},
        {School:{
        college:"M.V.Raman High School",
        qualification:"Secondary School Education",
        details:{
        location:"Atmakur, Wanaparthy, Telangana",
        duration:"07-2021 - 07-2024",
        grade:"CGPA- 9.5"
        }
    }
},
        {Intermediate:{
        college:"Institute Of Electronics",
        qualification:"Diploma in Embedded Systems",
        details:{
        location:"Atmakur, Wanaparthy, Telangana",
        duration:"07-2021 - 07-2024",
        grade:"CGPA- 9.5"
        }
    }
    }
]
  const [EducationDetails,setEducationDetails]=useState(EducationData[BTech]);
  const handleClick=(data)=>{
    setEducationDetails(data)
  }
  return (
    <div className='Education'>
        <div className='EduLinks'>
            <button onClick={()=>handleClick(EducationData[0])}>BTech</button>
            <button onClick={()=>handleClick(EducationData[1])}>School</button>
            <button onClick={()=>handleClick(EducationData[2])}>Intermediate</button>
        </div>
        <div className='School'>
            <div className='Name'>{EducationDetails.college}</div>
            <div className='Qualification'>{EducationDetails.qualification}</div>
            <div className='Details'>
                <ul>
                    {Object.keys(EducationDetails.details).map((key=>(
                        <li>{EducationDetails.details[key]}</li>
                    )))}
                    
                </ul>
            </div>
            <div className='School'>
            <div className='Name'>Gokaraju Rangaraju Institute Of Engineering & Technology</div>
            <div className='Qualification'>B.Tech in Information Technology</div>
            <div className='Details'>
                <ul>
                    <li>CGPA - 9.0</li>
                    <li>07-2021 to 06-2024</li>
                    <li>Kukatpally ,Hyderabad, Telangana</li>
                </ul>
            </div>
            </div>
        </div>
    </div>
  )
}
